-- 创建notification_configs表
CREATE TABLE notification_configs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    config_type TEXT NOT NULL UNIQUE CHECK(config_type IN ('telegram', 'ntfy', 'dingtalk', 'email', 'desktop')),
    enabled BOOLEAN NOT NULL DEFAULT FALSE,
    config_data TEXT NOT NULL,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP
);

-- 创建monitor_configs表
CREATE TABLE monitor_configs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    wallet_address TEXT NOT NULL UNIQUE,
    selected_chains TEXT NOT NULL,
    monitor_items TEXT NOT NULL,
    notification_methods TEXT NOT NULL,
    thresholds TEXT NOT NULL,
    is_enabled BOOLEAN NOT NULL DEFAULT TRUE,
    status TEXT NOT NULL DEFAULT 'inactive' CHECK(status IN ('inactive', 'configured', 'monitoring', 'stopped')),
    last_update DATETIME,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP
);

-- 创建monitor_history表
CREATE TABLE monitor_history (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    monitor_config_id INTEGER NOT NULL,
    wallet_address TEXT NOT NULL,
    chain_key TEXT NOT NULL,
    monitor_type TEXT NOT NULL CHECK(monitor_type IN ('balance', 'nonce', 'transaction')),
    old_value TEXT,
    new_value TEXT NOT NULL,
    change_amount TEXT,
    transaction_hash TEXT,
    block_number INTEGER,
    notification_sent BOOLEAN NOT NULL DEFAULT FALSE,
    notification_methods TEXT NOT NULL,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (monitor_config_id) REFERENCES monitor_configs(id) ON DELETE CASCADE
);

-- 创建proxy_configs表
CREATE TABLE proxy_configs (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    proxy_type TEXT NOT NULL CHECK(proxy_type IN ('http', 'socks5')),
    host TEXT NOT NULL,
    port INTEGER NOT NULL,
    username TEXT,
    password TEXT,
    is_active BOOLEAN NOT NULL DEFAULT TRUE,
    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP
);

-- 创建public_schema_migrations表
CREATE TABLE public_schema_migrations (
    version INTEGER PRIMARY KEY,
    name TEXT NOT NULL,
    checksum TEXT NOT NULL,
    app_version TEXT NOT NULL,
    applied INTEGER NOT NULL DEFAULT 1,
    applied_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP
);

-- 创建索引
CREATE INDEX idx_rpc_providers_chain_id ON rpc_providers(chain_id);
CREATE INDEX idx_tokens_chain_id ON tokens(chain_id);
CREATE INDEX idx_monitor_history_config_id ON monitor_history(monitor_config_id);
CREATE INDEX idx_chains_ecosystem ON chains(ecosystem);
CREATE INDEX idx_rpc_providers_priority ON rpc_providers(priority);
CREATE INDEX idx_tokens_symbol ON tokens(symbol);
CREATE INDEX idx_notification_configs_type ON notification_configs(config_type);
CREATE INDEX idx_monitor_configs_address ON monitor_configs(wallet_address);
CREATE INDEX idx_monitor_configs_status ON monitor_configs(status);
CREATE INDEX idx_monitor_history_address ON monitor_history(wallet_address);
CREATE INDEX idx_monitor_history_chain ON monitor_history(chain_key);
CREATE INDEX idx_monitor_history_type ON monitor_history(monitor_type);
CREATE INDEX idx_monitor_history_created_at ON monitor_history(created_at);
